import { useState, useEffect } from "react";

const gifs = ['./assets/images/tv-static.gif','./assets/images/B797.gif','./assets/images/7UIP.gif'];

const GlitchEffect = () => {
    const [listGifs, setListGifs] = useState(false) ;
    const [currentGif, setCurrentGif] = useState(gifs[0]) ;

    useEffect (() => {
        const gifOrder = setInterval(() => {
            const randomOrder = Math.floor(Math.random() * gifs.length) ;
            setCurrentGif(gifs[randomOrder]) ;
            setListGifs(true) ;

            setTimeout(() => {
                setListGifs(false) ;
            },500);

        }, 3000);

        return () => clearInterval(gifOrder) ;
    },[]);

    return(
        <div 
        style ={{
            backgroundColor: '#3014fc',
            width: '100%',
        height: '100vh',
        position:'relative'


        }}
        >

            {listGifs && (
            <img src={currentGif} alt="glitch" style={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            objectFit: 'cover',
            pointerEvents: 'none'
            }}
            />
    )}
    </div>

    );
};

export default GlitchEffect;
